import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class ReviewTests {

    @Test
    public void constructorTests(){
        //valid rating and comment
        Review r = new Review(2, "eh. not great", "ker", "ooh la la");
        assertNotNull(r);
        assertEquals(2, r.getRating());
        assertEquals("eh. not great", r.getSummary());
        assertEquals("ker", r.getAuthor());
        assertEquals("ooh la la", r.getGameTitle());

        //invalid rating, too low (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(0, "eh. nope", "ker", "ooh la la"));

        //invalid rating, too low
        assertThrows(IllegalArgumentException.class, () -> new Review(-3, "eh. nope", "ker", "ooh la la"));

        //invalid rating, too high (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(6, "eh. nope", "ker", "ooh la la"));

        //invalid rating, too high
        assertThrows(IllegalArgumentException.class, () -> new Review(10, "eh. nope", "ker", "ooh la la"));

        //valid rating, lower border
        r = new Review(1, "1 is the lowest valid rating", "ker", "ooh la la");
        assertNotNull(r);
        assertEquals(1, r.getRating());

        //valid rating, upper border
        r = new Review(5, "5 is the highest valid rating", "ker", "ooh la la");
        assertNotNull(r);
        assertEquals(5, r.getRating());

        //invalid comment, too short (border/only case)
        assertThrows(IllegalArgumentException.class, () -> new Review(3, "", "ker", "ooh la la"));

        //invalid comment, too long (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(3, "", "ker", "ooh la la"));

        //invalid comment, too long
        assertThrows(IllegalArgumentException.class, () -> new Review(3, "", "ker", "ooh la la"));

        //valid comment, lower border
        r = new Review(5, "k", "ker", "ooh la la");
        assertNotNull(r);
        assertEquals(1, r.getSummary().length());

        //valid comment, upper border
        r = new Review(5, "", "ker", "ooh la la");
        assertNotNull(r);
        assertEquals(10000, r.getSummary().length());

    }
}
