import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class ReviewTests {

    @Test
    public void constructorTests(){
        //valid rating and comment
        Review r = new Review(2, "eh. not great");
        assertNotNull(r);
        assertEquals(2, r.getRating());
        assertEquals("eh. not great", r.getComment());

        //invalid rating, too low (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(0, "eh. nope"));

        //invalid rating, too low
        assertThrows(IllegalArgumentException.class, () -> new Review(-3, "eh. nope"));

        //invalid rating, too high (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(6, "eh. nope"));

        //invalid rating, too high
        assertThrows(IllegalArgumentException.class, () -> new Review(10, "eh. nope"));

        //valid rating, lower border
        r = new Review(1, "1 is the lowest valid rating");
        assertNotNull(r);
        assertEquals(1, r.getRating());

        //valid rating, upper border
        r = new Review(5, "5 is the highest valid rating");
        assertNotNull(r);
        assertEquals(5, r.getRating());

        //invalid comment, too short (border/only case)
        assertThrows(IllegalArgumentException.class, () -> new Review(3, ""));

        //invalid comment, too long (border)
        assertThrows(IllegalArgumentException.class, () -> new Review(3, ""));

        //invalid comment, too long
        assertThrows(IllegalArgumentException.class, () -> new Review(3, ""));

        //valid comment, lower border
        r = new Review(5, "k");
        assertNotNull(r);
        assertEquals(1, r.getComment().length());

        //valid comment, upper border
        r = new Review(5, "");
        assertNotNull(r);
        assertEquals(10000, r.getComment().length());

    }
}
